<template>  <div id="detail">    <div class="imgList">      <div class="imgBox" v-for="item in imgList">        <img :src="item.src" @click="toPicture(item.id)" ondragstart="return false">      </div>    </div>  </div></template><script>import imgList from '../../images';export default {  components: {},  data (){    return {      imgList: [        ...imgList,      ],    }  },  mounted() {},  methods: {    toPicture(id) {      this.$router.push({        name: '图片',        query: {          id,        },      })    },  },}</script><style scoped lang="scss">@import "../../styles/mainVariables";#detail {  max-width: 1366px;  margin: 0 auto;  padding-top: 50px;}.imgList{  overflow: hidden;}.imgBox {  float: left;  width: 100px;  height: 100px;  margin: 20px 50px;  cursor: pointer;  img {    width: 100%;    height: 100%;  }}</style>